import { useEffect } from "react";

export function useKey(key) {
  useEffect(
    function () {
      function callBack(e) {
        if (e.code === key) {
          onCloseMovie();
        }
      }

      document.addEventListener("keydown", callBack);
      return function () {
        document.removeEventListener("keydown", callBack);
      };
    },
    [onCloseMovie]
  );
}
